import React from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import * as Yup from "yup";
import Button from "../Button";
import { IProfessional } from "../../alias/IProfessional";
import { ITypeOfProfessional } from "../../alias/ITypeOfProfessional";

const schema = Yup.object().shape({
  name: Yup.string().min(4).max(150).required(),
  email: Yup.string().email().min(4).max(150).required(),
  telephone: Yup.string().min(10).max(25).required(),
  typeOfProfessional: Yup.string().required(),
  situation: Yup.boolean(),
});

interface IProps {
  onSubmit(value: IProfessional, action: FormikHelpers<IProfessional>): void;
  loading: boolean;
  initialValues?: IProfessional;
  typeOfProfessionals: ITypeOfProfessional[];
}

const defaultValues: IProfessional = {
  name: "",
  email: "",
  telephone: "",
  typeOfProfessional: {} as ITypeOfProfessional,
  situation: false,
};

const FormProfessional: React.FC<IProps> = ({
  onSubmit,
  loading,
  initialValues = defaultValues,
  typeOfProfessionals,
}) => {
  function returnSelected(id: string) {
    if (initialValues && initialValues.id === id) {
      return true;
    }

    false;
  }

  return (
    <Formik
      initialValues={initialValues}
      validationSchema={schema}
      onSubmit={(values, action: FormikHelpers<IProfessional>) => {
        onSubmit(values, action);
      }}
    >
      <Form>
        <div className="form-group">
          <label htmlFor="nameInput">Name</label>
          <Field
            name="name"
            data-testid="name"
            type="text"
            className="form-control"
            id="nameInput"
            placeholder="Enter name"
          />
          <small className="form-text text-danger">
            <ErrorMessage name="name" />
          </small>
        </div>

        <div className="form-group">
          <label htmlFor="emailInput">Email address</label>
          <Field
            name="email"
            data-testid="email"
            type="email"
            className="form-control"
            id="emailInput"
            placeholder="Enter email"
          />
          <small className="form-text text-danger">
            <ErrorMessage name="email" />
          </small>
        </div>

        <div className="form-group">
          <label htmlFor="emailInput">Telephone</label>
          <Field
            name="telephone"
            data-testid="telephone"
            type="text"
            className="form-control"
            id="emailInput"
            placeholder="Enter telephone"
          />
          <small className="form-text text-danger">
            <ErrorMessage name="telephone" />
          </small>
        </div>

        <div className="form-group">
          <label htmlFor="typeOfProfessionalSelect">Type Of Professional</label>
          <Field
            as="select"
            name="typeOfProfessional"
            data-testid="typeOfProfessional"
            className="form-control"
            id="typeOfProfessionalSelect"
          >
            <option value={undefined}>select a profession type</option>
            {typeOfProfessionals.map((top) => (
              <option value={top.id}>{top.description}</option>
            ))}
          </Field>
          <small className="form-text text-danger">
            <ErrorMessage name="typeOfProfessional" />
          </small>
        </div>

        <div className="form-group">
          <label htmlFor="situationSelect">Situation</label>
          <Field
            as="select"
            name="situation"
            data-testid="situation"
            className="form-control"
            id="situationSelect"
          >
            <option value="true">Active</option>
            <option value="false">Inactive</option>
          </Field>
          <small className="form-text text-danger">
            <ErrorMessage name="situation" />
          </small>
        </div>

        <Button type="submit" loading={loading} className="btn-block mt-4 mb-3">
          Submit
        </Button>
      </Form>
    </Formik>
  );
};

export default FormProfessional;
