import React from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import * as Yup from "yup";
import Button from "../Button";
import { ITypeOfProfessional } from "../../alias/ITypeOfProfessional";

const schema = Yup.object().shape({
  description: Yup.string().min(4).max(150).required(),
  situation: Yup.boolean(),
});

interface IProps {
  onSubmit(
    value: ITypeOfProfessional,
    action: FormikHelpers<ITypeOfProfessional>
  ): void;
  loading: boolean;
  initialValues?: ITypeOfProfessional;
}

const defaultValues: ITypeOfProfessional = {
  description: "",
  situation: false,
};

const FormTypeOfProfessional: React.FC<IProps> = ({
  onSubmit,
  loading,
  initialValues = defaultValues,
}) => {
  return (
    <Formik
      initialValues={initialValues}
      validationSchema={schema}
      onSubmit={(values, action: FormikHelpers<ITypeOfProfessional>) => {
        onSubmit(values, action);
      }}
    >
      <Form>
        <div className="form-group">
          <label htmlFor="descriptionInput">Description</label>
          <Field
            name="description"
            data-testid="description"
            type="text"
            className="form-control"
            id="descriptionInput"
            placeholder="Enter description"
          />
          <small className="form-text text-danger">
            <ErrorMessage name="description" />
          </small>
        </div>

        <div className="form-group">
          <label htmlFor="situationSelect">Situation</label>
          <Field
            as="select"
            name="situation"
            data-testid="situation"
            className="form-control"
            id="situationSelect"
          >
            <option value="true">Active</option>
            <option value="false">Inactive</option>
          </Field>
          <small className="form-text text-danger">
            <ErrorMessage name="situation" />
          </small>
        </div>

        <Button type="submit" loading={loading} className="btn-block mt-4 mb-3">
          Submit
        </Button>
      </Form>
    </Formik>
  );
};

export default FormTypeOfProfessional;
