//import axios from "axios";

import axios from "axios";
import { IProfessional } from "../alias/IProfessional";

var fakeData: IProfessional[] = [
  {
    id: "1",
    name: "Lucas Carvalho",
    email: "lucas.carvalho@gopress.com",
    telephone: "(88) 88888-8888",
    typeOfProfessional: "1",
    situation: true,
  },
  {
    id: "2",
    name: "222222 Carvalho",
    email: "22222.carvalho@gopress.com",
    telephone: "(88) 88888-8888",
    typeOfProfessional: "1",
    situation: true,
  },
  {
    id: "3",
    name: "333333 Carvalho",
    email: "33333333.carvalho@gopress.com",
    telephone: "(88) 88888-8888",
    typeOfProfessional: "1",
    situation: true,
  },
  {
    id: "4",
    name: "4444444 Carvalho",
    email: "4444444.carvalho@gopress.com",
    telephone: "(88) 88888-8888",
    typeOfProfessional: "1",
    situation: true,
  },
];

export const getProfessionals = () => axios.get("/professionals");

export const getProfessional = (id: string) =>
  axios.get("/professionals/" + id);

export function editProfessional(
  id: string,
  data: IProfessional
): Promise<IProfessional> {
  return new Promise(async function (resolve, reject) {
    setTimeout(() => {
      let find = false;
      fakeData = fakeData.map((p) => {
        if (id === p.id) {
          data.id = id;
          find = true;
          return data;
        }
        return p;
      });

      if (!find) {
        reject();
      }

      resolve(data);
    }, 1000);
  });
}

export function createProfessional(
  data: IProfessional
): Promise<IProfessional> {
  return new Promise(async function (resolve, reject) {
    setTimeout(() => {
      data.id = "now" + Math.random();
      fakeData.push(data);
      resolve(data);
    }, 1000);
  });
}

export function deleteProfessional(id: string): Promise<String> {
  return new Promise(async function (resolve, reject) {
    setTimeout(() => {
      let find = false;
      fakeData = fakeData.filter((p) => {
        if (id === p.id) {
          find = true;
          return false;
        }
        return true;
      });

      if (!find) {
        reject();
      }

      resolve(id);
    }, 1000);
  });
}
